01030 
01031 void TMXBatemanDecay::ConsiderTransmutedElements(std::string LogicVolumeName) {
01032    for(int index=0; index<2; ++index){
01033        for (std::unordered_map<int, std::unordered_map<int,long double>>::iterator it_mother=fMapLVMotherDaughter_transm[index][LogicVolumeName].begin(); it_mother!=fMapLVMotherDaughter_transm[index][LogicVolumeName].end(); ++it_mother) {
01034            for (std::unordered_map<int,long double>::iterator it_daughter=fMapLVMotherDaughter_transm[index][LogicVolumeName][it_mother->first].begin(); it_daughter!=fMapLVMotherDaughter_transm[index][LogicVolumeName][it_mother->first].end(); ++it_daughter) {
01035 //                     std::cout << "ConsiderTransm it_mother->first : " << it_mother->first << " * it_daughter->second : " << it_daughter->second << " = results : " << it_daughter->second/(index+1.) << std::endl;
01036                 if(fMapN_0[it_mother->first] - it_daughter->second/(index+1.) > 0) {  // If the mother element is not entirely consumed
01037                     fMapN_0[it_mother->first] -= it_daughter->second/(index+1.); // index = 0 -> 1 reaction product | index = 1 -> 2 reaction products
01038                     fMapN_0[it_daughter->first] += it_daughter->second;
01039                 }
01040                 else { // We cannot produce a number of daughter higher than the number of mother element
01041                     fMapN_0[it_daughter->first] += fMapN_0[it_mother->first];
01042                     fMapN_0.erase(it_mother->first);
01043                 }
01044             }
01045         }
01046     }
01047 }
