00035 void TMXInputParameters::read(const std::string& strFileName) {
00036     std::ifstream myfile;
00037     myfile.open (strFileName);
00038 
00039     std::string strLine, separator = ",";
00040     std::vector<std::string> vTmp;
00041 
00042     while(std::getline (myfile,strLine)) {
00043 
00044         std::size_t lastPos = strLine.find(separator, 0), previousPos=0;
00045         vTmp.clear();
00046         while(lastPos != std::string::npos) {
00047 
00048             vTmp.push_back( strLine.substr(previousPos, lastPos-previousPos) );
00049             previousPos = lastPos+1;
00050             lastPos = strLine.find(separator, previousPos);
00051         }
00052         vTmp.push_back( strLine.substr(previousPos, lastPos-previousPos) );
00053 
00054         // Initialization
00055         ParameterInitialization(vTmp);
00056     }
00057     myfile.close();
00058 }
