00060 double TMXInputParameters::UnitConversion(const std::string& strUnit) {
00061 #ifndef M_PIl
00062 # define M_PIl          3.141592653589793238462643383279502884L /* pi */
00063 #endif
00064              if(strUnit.compare("mW") == 0) {return 1E-9;}
00065         else if(strUnit.compare( "W") == 0) {return 1E-6;}
00066         else if(strUnit.compare("kW") == 0) {return 1E-3;}
00067         else if(strUnit.compare("MW") == 0) {return 1E+0;}  // Default unit is MW
00068         else if(strUnit.compare("GW") == 0) {return 1E+3;}
00069         else if(strUnit.compare("TW") == 0) {return 1E+6;}
00070 
00071         else if(strUnit.compare("nm") == 0) {return 1E-6;}
00072         else if(strUnit.compare("um") == 0) {return 1E-3;}
00073         else if(strUnit.compare("mm") == 0) {return 1E+0;}  // Default G4 unit is mm
00074         else if(strUnit.compare("cm") == 0) {return 1E+1;}
00075         else if(strUnit.compare("dm") == 0) {return 1E+2;}
00076         else if(strUnit.compare("m" ) == 0) {return 1E+3;}
00077 
00078         else if(strUnit.compare("ns") == 0) {return 1E-9;}  // WARNING Default unit in G4 is ns, conversion is required if used in a G4 class
00079         else if(strUnit.compare("us") == 0) {return 1E-6;}
00080         else if(strUnit.compare("ms") == 0) {return 1E-3;}
00081         else if(strUnit.compare("s" ) == 0) {return 1E+0;}  // Default unit is s
00082         else if(strUnit.compare("mn") == 0) {return 1E+0*60;}
00083         else if(strUnit.compare("h" ) == 0) {return 1E+0*3600;}
00084         else if(strUnit.compare("d" ) == 0) {return 1E+0*3600*24;}
00085         else if(strUnit.compare("mo") == 0) {return 1E+0*3600*24*365.25/12.;}
00086         else if(strUnit.compare("y" ) == 0) {return 1E+0*3600*24*365.25;}
00087 
00088         else if(strUnit.compare( "eV") == 0) {return 1E-6;}
00089         else if(strUnit.compare("keV") == 0) {return 1E-3;}
00090         else if(strUnit.compare("MeV") == 0) {return 1E+0;}  // Default G4 unit is MeV
00091         else if(strUnit.compare("GeV") == 0) {return 1E+3;}
00092 
00093         else if(strUnit.compare("rad") == 0) {return 180. / M_PIl /*C++20 => std::numbers::pi*/;}
00094 
00095         else if(strUnit.compare(""  ) == 0) {return 1;}
00096         else { return 1;}
00097 }
