00295         std::vector < std::string > vec_raw;
00296         for(size_t i = 1; i<vString.size(); ++i) { if(vString[i] != "\r" && vString[i] != "\n" && vString[i] != "\r\n" && vString[i] != "") {vec_raw.push_back(vString[i]);} }
00297         CoreConfig.push_back(vec_raw);
00298     }
