00467 void TMXInputParameters::Initialize() {
00468     boolVisualization= 0;// Activation of the visualization (True or False)
00469     nThreads= 0;         // Number of Threads if multithreading is activated
00470     isThreadMerging = true; // Activation of thread merging into a ROOT file per run
00471     Verbose= 0;          // Verbose of the scoring manager
00472     BeamEnergy= 0;       // Energy of the beam particles
00473     Nparticles= 0;       // Number of particles to simulate
00474     Nrun= 0;             // Number of run to consider burn-up
00475     Ncycle= 1;           // Number of cycles to consider for keff computation with iteration power method option 20
00476     TimeOfRun= 0;        // Real time of 1 run [s]
00477     CoolingTime= 0;      // Cooling time to consider at the end of the runs [s]
00478     P_beam= 0;           // Beam power [MW]
00479     P_core= 0;           // Core power [MW]
00480     Geometry= 0;         // Geometry to be used in the calculation ( 0 = INR Trotsk | 1 = Rez)
00481     str_ROOTFileName= ""; // Name of the output ROOT file
00482     int_Option= 0;       // Simulation of a part of the geometry (0 = all | 1 = target only | 2 = target+lead )
00483     physName= "";         // Name of the physical model to use
00484     isAbla= 0;           // Use of ABLA de-excitation model
00485     isRandomSeed= 0;     // Use of Random seed for the calculation
00486     isLEND= 0;           // Use of LEND for photonuclear process (needs LEND database with G4LENDDATA="pathToLendData"
00487     PrintModulo= 0;      // Print the calculation status every PrintModulo events
00488     TemperatureWorld= 0; // Temperature of every material except the fuel
00489     TemperatureFuel= 0;  // Temperature of the fuel
00490     NbDiscardCycle= 0;   // Number of cycle to discard from the keff calculation with option 20
00491     ErrorLimitMCCubicVolume = 0; // Error upper limit on the estimation of the volume with the Monte Carlo method with option 30
00492     MCCubicVolumeLVname = ""; // Logical Volume cubic volume estimation by slice along Z axis using parallelWorld definition option 30 with a logical volume name
00493     ExtSourcePath= "";    // Path to the external source. If filled, an external source will be used.
00494     worldR= 0;           // Radius of the concrete volume
00495     worldZ= 0;           // Half height of the concrete volume // 5665.
00496     h0= 0;               // Height between the bottom of the pit and the center of the world volume (concrete) // 1964.5*mm
00497     // The target tube
00498     XtargetPosition= 0;  // Position along the beam axis (0 = center of the driver fuel)
00499     CheckGap= 0;         // Target tube thickness
00500     check1r= 0;          // Tube internal radius
00501     // The target plates
00502     xTarget= 0;          // half Target thickness  // initial = 10 mm
00503     yTarget= 0;          // half Target width // initial = 48 mm
00504     zTargetHeight= 0;    // half Target height (Oz) // initial = 252 mm
