00510     Target_Al_Ygap= 0;   // Gap between target plates and Aluminium parts along Y-axis, Initial = 1 mm, 5 mm a mettre
00511     Al_thickness= 0;     // Thickness of Aluminium part (empty structure), Initial = 1 mm
00512     Al_Tube_gap= 0;      // Gap between Aluminium parts and the tube containing the target (fLogicCheck1U), Initial = 1.5 mm
00513     // Air on the top of the pit
00514     airtopR= 0;                           // Radius of the air cylinder
00515     AirTopZdistanceFromBottomtoTop= 0;    // distance between the bottom of the pit and the top of the air cylinder
00516     // Water-cooled heat shield (reflector in the pit)
