00522     cool10R= 0;                           // Radius of the experimental area
00523     cool10BottomGap= 0;                   // air gap at the bottom between the water-cooled heat shield (reflector) and the lead volume
00524     // Fuel in the driver area
00525     CoreShape= "";            // Shape of sub-critical core (Hexagonal or Cylindrical)
00526     FuelPlacement= "";        // Hexagonal or Cylindrical placement of the fuel
