00530     FuelZ= 0;                // half Height of the fuel
00531     dbl_PolygoneWidth= 0;    // width between 2 opposite sides of a fuel rod, not the maximum of of the polygon
00532     FuelName= "";             // Name of the fuel rod to use
00533     int_Nreplicate= 0;       // Number of fuel elements per layer
00534     int_Nlayer= 0;           // Number of layer for fuel assembly
00535     dbl_rRodsGap= 0;         // Gap between 2 fuel rods
