00015 string bonify(string sl) {
00016   transform(sl.begin(), sl.end(), sl.begin(), ::tolower);
00017   sl.erase(std::remove_if(sl.begin(), sl.end(), ::isspace), sl.end());
00018   if(sl.substr(sl.length()-1,1) == ".") sl.pop_back();
00019   return sl;
00020 }
