00023 int main() {
00024   ifstream carfile;
00025   ofstream outfile;
00026   string line;
00027   string patch;
00028   string deck;
00029   string keep;
00030   string seq;
00031   
00032   carfile.open("xnsect.car",std::ios_base::in);
00033   while(getline(carfile, line)) {
00034     if(line.substr(0,6) == "+TITLE") {
00035       getline(carfile,line);
00036       cout << "Title is " << line << endl;
00037     } else if(line.substr(0,6) == "+PATCH") {
00038       patch = bonify(line.substr(7,line.length()-7));
00039       cout << "New Patch " << patch << endl;
00040     } else if(line.substr(0,5) == "+DECK") {
00041       deck = bonify(line.substr(6,line.length()-6));
00042       outfile.close();
00043       outfile.open(patch+"_"+deck+".F",std::ios_base::out);
00044       continue;
00045       cout << "New Deck " << deck << endl;
00046     } else if(line.substr(0,5) == "+KEEP") {
00047       keep = bonify(line.substr(6,line.length()-6));
00048       outfile.close();
00049       outfile.open(patch+"_"+keep+".inc",std::ios_base::out);
00050       continue;
00051       cout << "New Keep " << keep << endl;
00052       continue;
00053     } else if(line.substr(0,4) == "+SEQ") {
00054       seq = bonify(line.substr(5,line.length()-5));
00055       outfile << "#include \""+seq+".inc\"" << endl;
00056       cout << "Dumping seq " << seq << endl;
00057       continue;
00058     }
00059     outfile << line << endl;
00060   }
00061   outfile.close();
00062   carfile.close();
00063 return 0;
00064 }
