00112 void TMXkeff::computeKeffCycle()
00113 {
00114     m_fissionSourceBank.nextCycle();
00115     double nu = m_fissionSourceBank.getNumberOfFissionSites() / m_nbFission;
00116 
00117     m_kgenPerCycle.push_back( m_nbFissionPerCycle[m_currentCycle]         / (double)m_nbParticlePerCycle );
00118     m_kcolPerCycle.push_back( nu * m_nbCollisionsPerCycle[m_currentCycle] / (double)m_nbParticlePerCycle );
00119     m_ktrkPerCycle.push_back( nu * m_nbTrckLengthPerCycle[m_currentCycle] / (double)m_nbParticlePerCycle );
00120     m_kabsPerCycle.push_back( nu * m_nbAbsorptionPerCycle[m_currentCycle] / (double)m_nbParticlePerCycle );
00121 
00122 
00123     printCycleKeff();
00124     nextCycle();
00125 }
