00134 G4double TMXkeff::GetMaterialXS(const G4Step * theStep, std::string XSreaction)
00135 {
00136     const G4DynamicParticle* particleDyn = theStep->GetTrack()->GetDynamicParticle();
00137     particleDyn                          = new G4DynamicParticle(particleDyn->GetParticleDefinition(),
00138                                                                  particleDyn->GetMomentumDirection(),
00139                                                                  theStep->GetPreStepPoint()->GetKineticEnergy());
00140     G4Material *mat                      = theStep->GetPostStepPoint()->GetMaterial();
00141     G4ParticleDefinition *particle       = G4ParticleTable::GetParticleTable()->FindParticle("neutron");
00142     G4HadronicProcess * process          = (G4HadronicProcess *)particle->GetProcessManager()->GetProcess(XSreaction);
00143     delete particleDyn;
00144     return process->GetCrossSectionDataStore()->GetCrossSection(particleDyn, mat) * cm;
00145 }
