00154 void TMXkeff::inGeneratePrimaries(G4ParticleGun * particleGun, G4Event * anEvent)
00155 {
00156     if (m_currentCycle <= 1)
00157     {
00158         G4double x = 0;
00159         G4double y = 0;
00160         G4double z = 0;
00161         particleGun->SetParticlePosition(G4ThreeVector(x*cm, y*cm, z*cm));
00162         particleGun->SetParticleEnergy(14*MeV);
00163 
00164         G4double cosTheta = 2 * G4UniformRand() - 1.;
00165         G4double phi      = 2 * 3.14159 * G4UniformRand();
00166         particleGun->SetParticleMomentumDirection(G4ThreeVector(std::sqrt(1. - cosTheta * cosTheta)*std::cos(phi),std::sqrt(1. - cosTheta * cosTheta)*std::sin(phi),cosTheta));
00167         particleGun->GeneratePrimaryVertex(anEvent);
00168     }
00169     else
00170     {
00171         FissionSite site = TMXkeff::getInstance()->getFissionSite(m_samplingSourceNumber);
00172         if(m_samplingSourceNumber == m_nbParticlePerCycle || m_samplingSourceNumber == getNumberOfFissionSites() - 1) m_samplingSourceNumber = 0;
00173         else m_samplingSourceNumber++;
00174         particleGun->SetParticlePosition(site.position);
00175         particleGun->SetParticleEnergy(site.energy);
00176         particleGun->SetParticleMomentumDirection(site.direction);
00177         particleGun->GeneratePrimaryVertex(anEvent);
00178     }
00179 }
