00186 void TMXkeff::inUserSteppingAction(const G4Step * theStep)
00187 {
00188 
00189     if(theStep->GetPostStepPoint()->GetPhysicalVolume())
00190     {
00191         G4AutoLock lock(&myHEPPrimGenMutex);
00192         G4Track* theTrack                  = theStep->GetTrack();
00193 
00194         G4StepPoint* thePostPoint          = theStep->GetPostStepPoint();
00195         G4String processName               = thePostPoint->GetProcessDefinedStep()->GetProcessName();
00196         G4ParticleDefinition* particleType = theTrack->GetDefinition();
00197         G4String particleName              = particleType->GetParticleName();
00198 
00199         G4double MaterialSigmaF            = GetMaterialXS(theStep, "nFission");
00200         G4double MaterialSimaCap           = GetMaterialXS(theStep, "nCapture");
00201         G4double MaterialSigmaAbs          = MaterialSigmaF
00202                                            + MaterialSimaCap;
00203         G4double MaterialSigmaTot          = GetMaterialXS(theStep, "hadElastic")
00204                                            + GetMaterialXS(theStep, "neutronInelastic")
00205                                            + MaterialSigmaAbs;
00206 
00207         if(processName == "nCapture" ) m_countCapture++;
00208         if(processName == "hadElastic" ) m_countElastic++;
00209         if(processName == "neutronInelastic" ) m_countInelastic++;
00210         if(processName == "Transportation" ) m_countTransportation++;
00211 
00212         if(processName == "nFission" || processName == "nCapture")
00213         {
00214             addAbsorption(MaterialSigmaF/MaterialSigmaAbs);
00215         }
00216 
00217         if ( theStep->GetPostStepPoint()->GetStepStatus() != fGeomBoundary )
00218         {
00219             addCollision( MaterialSigmaF / MaterialSigmaTot);
00220         }
00221 
00222         if (true)
00223         {
00224             addTrackLength(MaterialSigmaF * (theStep->GetStepLength() / cm));
00225         }
00226 
00227         if(processName == "nFission")
00228         {
00229             const std::vector<const G4Track*> *secondaries = theStep->GetSecondaryInCurrentStep();
00230             int countSecNeutrons = 0;
00231             for(G4int i=0; i<(G4int)secondaries->size(); ++i)
00232             {
00233                 G4String secondary_particleName = (*secondaries)[i]->GetDefinition()->GetParticleName();
00234                 if (secondary_particleName == "neutron")
00235                 {
00236                         countSecNeutrons++;
00237                         addFissionSite(thePostPoint->GetPosition(), (*secondaries)[i]->GetMomentumDirection(), (*secondaries)[i]->GetKineticEnergy(), (*secondaries)[i]->GetMomentum());
00238                 }
00239             }
00240             m_fissionEnergy += theStep->GetPreStepPoint()->GetKineticEnergy() / MeV;
00241             m_countFission++;
00242             addFission(countSecNeutrons);
00243             theTrack->SetTrackStatus(fKillTrackAndSecondaries);
00244             return;
00245             }
00246         }
00247 }
