00254 double TMXkeff::computeMeanStdev(std::vector<double> k)
00255 //To be corrected. Wrong
00256 {
00257     double sum         = 0.;
00258     double sumSquare   = 0.;
00259     if ((int)k.size() <= m_nbCycleToDiscard) return 0.;
00260     for (std::vector<double>::size_type i = m_nbCycleToDiscard; i < k.size(); i++)
00261     {
00262         sumSquare     += k[i] * k[i];
00263         sum           += k[i];
00264     }
00265     double mean        = sum / (k.size() - m_nbCycleToDiscard);
00266     double mean2       = sumSquare / (k.size() - m_nbCycleToDiscard);
00267     return sqrt(mean2 - mean * mean) / (k.size() - 1 - m_nbCycleToDiscard);
00268 }
