00270 
00276 double TMXkeff::computeMean(std::vector<double> k)
00277 {
00278     double average = 0.;
00279     if ((int)k.size() <= m_nbCycleToDiscard) return 0.;
00280     for (std::vector<double>::size_type i = m_nbCycleToDiscard; i < k.size(); i++)
00281         average += k[i];
00282     return average / (k.size() - m_nbCycleToDiscard);
00283 }
