00289 void TMXkeff::nextCycle()
00290 {
00291     std::cout<< "Nu x Fis : " << (double)m_countFission / (double)m_nbParticlePerCycle * (double)getNumberOfFissionSites() / (double)m_nbFission<<std::endl;
00292     std::cout<< "Nu       : " << (double)getNumberOfFissionSites() / (double)m_nbFission<<std::endl;
00293     std::cout<< "Fissions : " << (double)m_countFission / (double)m_nbParticlePerCycle<<std::endl;
00294     std::cout<< "Ener@Fiss: " << (double)m_fissionEnergy / (double)m_countFission<<std::endl;
00295     std::cout<< "Captures : " << (double)m_countCapture / (double)m_nbParticlePerCycle<<std::endl;
00296     std::cout<< "Elastics : " << (double)m_countElastic / (double)m_nbParticlePerCycle<<std::endl;
00297     std::cout<< "Inelasti : " << (double)m_countInelastic / (double)m_nbParticlePerCycle<<std::endl;
00298     std::cout<< "Transpor : " << (double)m_countTransportation / (double)m_nbParticlePerCycle<<std::endl;
00299     std::cout << "**************************************************************" << std::endl;
00300 
00301     m_countFission = 0;
00302     m_countCapture = 0;
00303     m_countElastic = 0;
00304     m_countInelastic = 0;
00305     m_countTransportation = 0;
00306     m_fissionEnergy = 0.;
00307 
00308 
00309 
00310     m_nbFissionPerCycle.push_back(0);
00311     m_nbCollisionsPerCycle.push_back(0);
00312     m_nbTrckLengthPerCycle.push_back(0);
00313     m_nbAbsorptionPerCycle.push_back(0);
00314     m_nbFission = 0;
00315     m_samplingSourceNumber = 0;
00316 
00317 
00318     m_currentCycle++;
00319 }
