00455 void TMXkeff::printCycleKeff()
00456 {
00457     writeKResults();
00458 
00459     std::cout <<                                                                     std::endl;
00460     std::cout << "**************************************************************" << std::endl;
00461     std::cout << "*********************  KEFF_CALCULATION  *********************" << std::endl;
00462     std::cout << "**************************************************************" << std::endl;
00463     if (m_currentCycle > m_nbCycleToDiscard )
00464     {
00465         std::cout << " Cycle: "        << m_currentCycle                          << std::endl;
00466         std::cout << " -- "                                                       << std::endl;
00467         std::cout << fixed << setprecision(6) << " Kgen-Cycle: "   << m_kgenPerCycle[m_currentCycle - 1]      << std::endl;
00468         std::cout << fixed << setprecision(6) << " Kcol-Cycle: "   << m_kcolPerCycle[m_currentCycle - 1]      << std::endl;
00469         std::cout << fixed << setprecision(6) << " Ktrk-Cycle: "   << m_ktrkPerCycle[m_currentCycle - 1]      << std::endl;
00470         std::cout << fixed << setprecision(6) << " Kabs-Cycle: "   << m_kabsPerCycle[m_currentCycle - 1]      << std::endl;
00471         std::cout << " -- "                                                       << std::endl;
00472         std::cout << fixed << setprecision(6) << " Kgen: "         << computeMean(m_kgenPerCycle)
00473                   << " Uncertainty: "  << computeMeanStdev(m_kgenPerCycle)         << std::endl;
00474         std::cout << fixed << setprecision(6) << " Kcoll: "        << computeMean(m_kcolPerCycle)
00475                   << " Uncertainty: "  << computeMeanStdev(m_kcolPerCycle)         << std::endl;
00476         std::cout << fixed << setprecision(6) << " Ktrk: "         << computeMean(m_ktrkPerCycle)
00477                   << " Uncertainty: "  << computeMeanStdev(m_ktrkPerCycle)         << std::endl;
00478         std::cout << fixed << setprecision(6) << " Kabs: "         << computeMean(m_kabsPerCycle)
00479                   << " Uncertainty: "  << computeMeanStdev(m_kabsPerCycle)         << std::endl;
00480     }
00481     else
00482     {
00483         std::cout << fixed << setprecision(6) << " Cycle: "        << m_currentCycle                          << std::endl;
00484         std::cout << fixed << setprecision(6) << " Kgen-Cycle: "   << m_kgenPerCycle[m_currentCycle - 1]      << std::endl;
00485         std::cout << fixed << setprecision(6) << " Kcol-Cycle: "   << m_kcolPerCycle[m_currentCycle - 1]      << std::endl;
00486         std::cout << fixed << setprecision(6) << " Ktrk-Cycle: "   << m_ktrkPerCycle[m_currentCycle - 1]      << std::endl;
00487         std::cout << fixed << setprecision(6) << " Kabs-Cycle: "   << m_kabsPerCycle[m_currentCycle - 1]      << std::endl;
00488     }
00489     std::cout << "**************************************************************" << std::endl;
00490     std::cout <<                                                                     std::endl;
00491 }
