00497 void TMXkeff::printAll()
00498 {
00499     cout.precision(5);
00500     std::cout << "-------------------------KEFF VALUES------------------------"   << std::endl;
00501     std::cout << std::left << std::setw(12) << "Cycle"    << std::setw(12) << "kgen"
00502                                                           << std::setw(12) << "kcol"
00503                                                           << std::setw(12) << "ktrk"
00504                                                           << std::setw(12) << "kabs" << std::endl;
00505     for (std::vector<double>::size_type i = 0; i < m_kcolPerCycle.size(); i++)
00506     {
00507         if((int)i == m_nbCycleToDiscard)
00508             std::cout << "================== ACTIVE CYCLES ==================" <<std::endl;
00509         std::cout << fixed << setprecision(6) << std::left << std::setw(12) << i + 1  << std::setw(12) << m_kgenPerCycle[i]
00510                                                            << std::setw(12) << m_kcolPerCycle[i]
00511                                                            << std::setw(12) << m_ktrkPerCycle[i]
00512                                                            << std::setw(12) << m_kabsPerCycle[i] << std::endl;
00513     }
00514     std::cout << "------------------------------------------------------------" << std::endl;
00515     std::cout << "Calculation performed for " << m_nbTotCycle << " cycles of "
00516               << m_nbFissionPerCycle[0] << " particles discarding " << m_nbCycleToDiscard << " cycles" << std::endl;
00517     std::cout << "------------------------------------------------------------" << std::endl;
00518     std::cout << fixed << setprecision(6) << std::left << std::setw(12) << "kgen: "   << std::setw(12) << computeMean(m_kgenPerCycle)
00519                                                           << " ( " << computeMeanStdev(m_kgenPerCycle) << " ) " << std::endl;
00520     std::cout << fixed << setprecision(6) << std::left << std::setw(12) << "kcol: "   << std::setw(12) << computeMean(m_kcolPerCycle)
00521                                                           << " ( " << computeMeanStdev(m_kcolPerCycle) << " ) " << std::endl;
00522     std::cout << fixed << setprecision(6) << std::left << std::setw(12) << "ktrk: "   << std::setw(12) << computeMean(m_ktrkPerCycle)
00523                                                           << " ( " << computeMeanStdev(m_ktrkPerCycle) << " ) " << std::endl;
00524     std::cout << fixed << setprecision(6) << std::left << std::setw(12) << "kabs: "   << std::setw(12) << computeMean(m_kabsPerCycle)
00525                                                           << " ( " << computeMeanStdev(m_kabsPerCycle) << " ) " << std::endl;
00526     std::cout << "------------------------------------------------------------" << std::endl;
00527 }
